import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getTodos, deleteTodo } from '../api';
import './styles.css'; // Import the CSS file

const HomePage = () => {
    const [todos, setTodos] = useState([]);
    const [filteredTodos, setFilteredTodos] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = async () => {
        try {
            const { data } = await getTodos();
            setTodos(data);
            setFilteredTodos(data); // Initialize filteredTodos with all todos
            setLoading(false);
        } catch (err) {
            console.error('Failed to fetch todos:', err);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteTodo(id);
            fetchTodos(); // Refresh the list after deletion
        } catch (err) {
            console.error('Failed to delete todo:', err);
        }
    };

    const handleSearch = () => {
        const filtered = todos.filter((todo) =>
            todo.title.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredTodos(filtered);
    };

    if (loading) return <p>Loading...</p>;

    return (
        <div className="container"> {/* Added container class */}
            <h1 className="title">Todo List</h1>
            <div className="search-container">
                <input
                    type="text"
                    className="search-input"
                    placeholder="Search by title"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button className="search-button" onClick={handleSearch}>Search</button>
            </div>
            <Link to="/add" className="add-todo-link">Add Todo</Link>
            <ul className="todo-list">
                {filteredTodos.map((todo) => (
                    <li key={todo._id} className="todo-item">
                        <h3 className="todo-title">{todo.title}</h3>
                        <p className="todo-description">{todo.description}</p>
                        <p className="todo-status">Status: {todo.status}</p>
                        <div className="button-group">
                            <Link to={`/edit/${todo._id}`} className="edit-button">Edit</Link>
                            <button className="delete-button" onClick={() => handleDelete(todo._id)}>Delete</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HomePage;
