import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createTodo } from '../api';

const AddTodo = () => {
    const [todo, setTodo] = useState({ title: '', description: '', status: 'pending' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setTodo({
            ...todo,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createTodo(todo);
            navigate('/');
        } catch (err) {
            console.error('Failed to create todo:', err);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Add Todo</h1>
            <label>
                Title:
                <input type="text" name="title" value={todo.title} onChange={handleChange} required />
            </label>
            <label>
                Description:
                <textarea name="description" value={todo.description} onChange={handleChange} />
            </label>
            <label>
                Status:
                <select name="status" value={todo.status} onChange={handleChange}>
                    <option value="pending">Pending</option>
                    <option value="in-progress">In Progress</option>
                    <option value="completed">Completed</option>
                </select>
            </label>
            <button type="submit">Add</button>
        </form>
    );
};

export default AddTodo;
