import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getTodo, updateTodo } from '../api';

const EditTodo = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [todo, setTodo] = useState({ title: '', description: '', status: '' });

    useEffect(() => {
        fetchTodo();
    }, []);

    const fetchTodo = async () => {
        try {
            const { data } = await getTodo(id);
            setTodo(data);
        } catch (err) {
            console.error('Failed to fetch todo:', err);
        }
    };

    const handleChange = (e) => {
        setTodo({
            ...todo,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateTodo(id, todo);
            navigate('/');
        } catch (err) {
            console.error('Failed to update todo:', err);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Edit Todo</h1>
            <label>
                Title:
                <input type="text" name="title" value={todo.title} onChange={handleChange} required />
            </label>
            <label>
                Description:
                <textarea name="description" value={todo.description} onChange={handleChange} />
            </label>
            <label>
                Status:
                <select name="status" value={todo.status} onChange={handleChange}>
                    <option value="pending">Pending</option>
                    <option value="in-progress">In Progress</option>
                    <option value="completed">Completed</option>
                </select>
            </label>
            <button type="submit">Update</button>
        </form>
    );
};

export default EditTodo;
